package com.amz;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

import java.util.*;

/**
 * @see "https://leetcode.com/problems/minimum-window-substring/"
 */
public class MinimumWindowSubstring {

    public String minWindow(String s, String t) {
        Map<Character, Integer> tgtCharCounts = new HashMap<>();
        List<Integer> indexes = new ArrayList<>();
        for (char c : t.toCharArray()) {
            tgtCharCounts.compute(c, (k, v) -> v == null ? 1 : v + 1);
        }
        for (int i = 0; i < s.length(); i++) {
            if (tgtCharCounts.containsKey(s.charAt(i))) {
                indexes.add(i);
            }
        }
        int requiredMatchCount = tgtCharCounts.size();
        String minWindow = null;
        int minWindowLength = Integer.MAX_VALUE;
        for (int i = 0; i < indexes.size(); i++) {
            Map<Character, Integer> charCounts = new HashMap<>();
            int start = indexes.get(i);
            int matchedCount = 0;
            for (int j = i; j < indexes.size(); j++) {
                int end = indexes.get(j);
                if (minWindowLength < end - start) break;
                char ch = s.charAt(end);
                int count = charCounts.compute(ch, (k, v) -> v == null ? 1 : v + 1);
                if (tgtCharCounts.get(ch) == count) matchedCount++;
                if (requiredMatchCount == matchedCount) {
                    String window = s.substring(start, end + 1);
                    if (minWindow == null || end - start < minWindowLength) {
                        minWindow = window;
                        minWindowLength = minWindow.length();
                        break;
                    }
                }
            }
        }
        return minWindow == null ? "" : minWindow;
    }

    @Test
    public void check1() {
        Assertions.assertEquals("BANC", minWindow("ADOBECODEBANC", "ABC"));
    }

    @Test
    public void check2() {
        Assertions.assertEquals("a", minWindow("a", "a"));
    }

    @Test
    public void check3() {
        Assertions.assertEquals("", minWindow("a", "aa"));
    }

    @Test
    public void check4() {
        Assertions.assertEquals("aa", minWindow("aa", "aa"));
    }

    @Test
    public void check5() {
        Assertions.assertEquals("", minWindow("aa", "aaa"));
    }

    @Test
    public void check6() {
        Assertions.assertEquals("", minWindow("a", "b"));
    }

    @Test
    public void check7() {
        Assertions.assertEquals("baa", minWindow("bbaa", "aba"));
    }

    @Test
    public void check8() {
        Assertions.assertEquals("", minWindow("",""));
    }

}
