package com.amz;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

/**
 * @see "https://leetcode.com/problems/repeated-substring-pattern/"
 */
public class RepeatedSubstringPattern {

    @Test
    public void t1() {
        verify("", false);
    }

    @Test
    void t2() {
        verify(null, false);
    }

    @Test
    void t3() {
        verify("a", false);
    }

    @Test
    void t4() {
        verify("ab", false);
    }

    @Test
    void t5() {
        verify("aa", true);
        verify("abab", true);
    }

    @Test
    void t6() {
        verify("abcabcabc", true);
    }

    @Test
    void t7() {
        verify("ababba", false);
    }

    @Test
    void t8() {
        verify("", false);
    }

    private void verify(String input, boolean expected) {
        Assertions.assertEquals(expected, repeatedSubstringPattern(input));
    }

    public boolean repeatedSubstringPattern(String s) {
        if (s == null) return false;
        final StringBuilder pattern = new StringBuilder("()+");
        for (int len = 1; len <= s.length() / 2; len++) {
            if (s.length() % len == 0) {
                pattern.insert(1, s, 0, len);
                if (s.matches(pattern.toString())) return true;
                pattern.delete(1, 1 + len);
            }
        }
        return false;
    }
}
